        //     .then((response) => { 
        //         setData(response) 
        //     console.log(response)})

        const file = fs.readFileSync("./data","utf-8")
        
    })
    return (
        <>
            {data.sort((a, b) => a - b).map((index) => {
                return (
                    <div className="card">
                        <img src={index.imageUrl} className="card-img-top" alt="..." />
                        <div className="card-body">
                            <h5 className="card-title">{index.name}</h5>
                            <p className="card-text">{index.messege}</p>
                            {/* <a href="#" className="btn btn-primary">Go somewhere</a> */}
                        </div>
                    </div>
                )
            })}
        </>
    )
}
